import React, { useState } from "react";
import NewExpenses from "../ExpenseForm/NewExpenses";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";
import ExpenseItem from "./ExpenseItem";

const Expenses = () => {
	const [expenses, setExpenses] = useState([
		{
			id: "e1",
			title: "Toilet Paper",
			amount: 94.12,
			date: new Date(2020, 7, 14),
		},
		{ id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
		{
			id: "e3",
			title: "Car Insurance",
			amount: 294.67,
			date: new Date(2021, 2, 28),
		},
		{
			id: "e4",
			title: "New Desk (Wooden)",
			amount: 450,
			date: new Date(2021, 5, 12),
		},
	]);
    const [yearSelected, setYearSelected] = useState('2020');
    const [subFilterText, setSubFilterText] = useState("Filter for 2019,2021 and 2022 are hidden"); 
	const expenseAdded = (expense) => {
		setExpenses({
			...expenses,
			expense,
		});
	};

    const yearReturned = (year) => {
        var selectedYearText = "";
        setYearSelected(year);
        if (year === '2019') {
            selectedYearText = "Filter for 2020, 2021 and 2022 are hidden"
         }
	};

	return (
		<>
			<NewExpenses addedExpense={expenseAdded} />
			<Card className="expenses">
                <ExpenseFilter initialSelectedYear={yearSelected} selectedYear={yearReturned} />
                <p className="subFilterText">{ subFilterText }</p>
				{expenses.map((item) => {
					return (
						<ExpenseItem
							itemDate={item.date}
							itemName={item.title}
							itemPrice={item.amount}
						/>
					);
				})}
			</Card>
		</>
	);
};

export default Expenses;
