import { useState } from "react";
import ExpenseForm from "./ExpenseForm";
import "./NewExpenses.css";

const NewExpenses = (props) => {
	const [formVisible,setFormVisbile] = useState(false);
	const onExpenseAdd = (expenseData) => {
		const expenseAdded = {
			...expenseData,
			id: Math.random().toString(),
		};
        props.addedExpense(expenseAdded);
	};
	return (
		<div className="new-expense">
			{
				formVisible ? <button className="new-expense__actions" onClick={ setFormVisbile(true)}>Add Expenses</button> : <ExpenseForm onSaveExpense={onExpenseAdd} />
			}
			
		</div>
	);
};

export default NewExpenses;
