import "./ExpenseItem.css";
import DateCard from "./DateCard";
import Card from "../UI/Card";
import { useState } from "react";
const ExpenseItem = (props) => {
	const [title, setTitle] = useState(props.itemName);
	const [visible, setVisible] = useState(true);
	function clickHandler(){ 
		setTitle("Changed	")
	}
	return (
		<Card className="expense-item">
			<DateCard data={props.itemDate} />
			<div className="expense-item__description">
				<h2>{title}</h2>
				<div className="expense-item__price">${props.itemPrice}</div>
				<button visible={ visible} onClick={clickHandler} className="expense-item__price">Change Title</button>
			</div>
		</Card>
	);
};

export default ExpenseItem;
